import React from 'react'
import { useState, useEffect } from 'react';
import BotonFiltro from './BotonFiltro';

const Catalogo = () => {
    
    
    const images = [
        { id: '1' , name: 'A - Simpsons/A001.jpg', tag: 'simpsons' },
        {id: '2', name:'img2', tag:'simpsons'},
        {id: '3', name:'img3', tag:'simpsons'},
        {id: '4', name:'img4', tag:'simpsons'},
        {id: '5', name:'img5', tag:'star wars'},
        {id: '6', name:'img6', tag:'star wars'},
        {id: '7', name:'img7', tag:'star wars'},

    ];
    

    const [tag, setTag] = useState('todos');
    const [filteredImages, setFilteredImages] = useState([]);

    useEffect( () => {
        if(tag === 'todos'){
            setFilteredImages(images)
        } else {
            setFilteredImages(images.filter( image => image.tag === tag))
            
        }
    })

    return (
        <div className='container-fluid'>
            <div className='row'>
                <div className='col-12 col-md-2 cuadro2 m-3 mt-5 indexCatalogo'>
                    <div className='tituloCatalogo text-center'>
                        Nuestro Catalogo
                    </div>
                    <div className='text-center'>
                        <BotonFiltro boton='todos' handleSetTag={setTag}/>
                        <BotonFiltro boton='simpsons' handleSetTag={setTag}/>
                        <BotonFiltro boton='star wars' handleSetTag={setTag}/>
                        <BotonFiltro boton='series & peliculas' handleSetTag={setTag}/>
                    </div>

                </div>

                <div className='col-12 col-md-2'>
                    {filteredImages.map( image => 
                    <div key={image.id}>
                        <ItemCatalogo />
                    </div>)}
                </div>

            </div>
            
        </div>
    )
}

export default Catalogo
